import { Command } from 'commander';

/**
 * Validate Command
 *
 * Validates an MCP server project:
 * - Checks tool registration in index.ts
 * - Verifies test files exist
 * - Validates integration test YAMLs
 * - Checks .mcp-template.json metadata
 */

/**
 * Validation issue severity
 */
type IssueSeverity = "error" | "warning" | "info";
/**
 * Validation issue
 */
interface ValidationIssue {
    severity: IssueSeverity;
    category: string;
    message: string;
    file?: string;
    suggestion?: string;
}
/**
 * Validation result
 */
interface ValidationResult {
    passed: boolean;
    issues: ValidationIssue[];
    summary: {
        errors: number;
        warnings: number;
        info: number;
    };
}
/**
 * Create validate command
 */
declare function createValidateCommand(): Command;
/**
 * Validate entire project
 */
declare function validateProject(cwd: string, options?: {
    fix?: boolean;
    strict?: boolean;
}): Promise<ValidationResult>;

export { type IssueSeverity, type ValidationIssue, type ValidationResult, createValidateCommand, validateProject };
